function fgcolor(color)
	setFgColor(color_table[color][1], color_table[color][2], color_table[color][3])
end

function bgcolor(color)
	setBgColor(color_table[color][1], color_table[color][2], color_table[color][3])
end

function cfgcolor(console, color)
	setFgColor(console, color_table[color][1], color_table[color][2], color_table[color][3])
end

function cbgcolor(console, color)
	setBgColor(console, color_table[color][1], color_table[color][2], color_table[color][3])
end

function showColours(num)
	cecho("\n<green> Showing a list of available colours, with "..num.." columns: \n")
	local lineBreak = num
	local linecount = 0
	local padding = math.floor(90/num)

	for col, tab in pairs(color_table) do
		linecount = linecount + 1
		cecho(string.rep(" ", padding-#col).."<"..col..">"..col.."<NavajoWhite>,")
		if linecount >= lineBreak then
			echo("\n")
			linecount = 0
		end
	end
end

bot = bot or {}

bot.info = {
room = "",
area = "",
next = ""
}

bot.new_room = false
bot.going = false

snd.class_list = { {class = "Templar", func = function(current) snd.templar_bash() end}, {class = "Luminary", func = function(current) snd.luminary_bash() end}, {class = "Earthcaller", func = function(current) snd.earthcaller_bash() end}, {class = "Ascendril", func = function(current) snd.ascendril_bash() end}, {class = "Bloodborn", func = function(current) snd.bloodborn_bash() end}, {class = "Zealot", func = function(current) snd.zealot_bash() end}, {class = "Ravager", func = function(current) snd.ravager_bash() end}, {class = "Shaman", func = function(current) snd.shaman_bash() end}, {class = "Sentinel", func = function(current) snd.sentinel_bash() end}, {class = "Monk", func = function(current) snd.monk_bash() end}, {class = "Wayfarer", func = function(current) snd.wayfarer_bash() end}, {class = "Syssin", func = function(current) snd.syssin_bash() end}, {class = "Shapeshifter", func = function(current) snd.shapeshifter_bash() end}, {class = "Archivist", func = function(current) snd.archivist_bash() end}, {class = "Sciomancer", func = function(current) snd.sciomancer_bash() end}, {class = "Teradrim", func = function(current) snd.teradrim_bash() end}, {class = "Tidesage", func = function(current) snd.tidesage_bash() end}, {class = "Praenomen", func = function(current) snd.praenomen_bash() end}, {class = "Akkari", func = function(current) snd.akkari_bash() end}, {class = "Indorani", func = function(current) snd.indorani_bash() end}, {class = "Oneiromancer", func = function(current) snd.oneiromancer_bash() end}, {class = "Carnifex", func = function(current) snd.carnifex_bash() end}, {class = "Revenant", func = function(current) snd.revenant_bash() end}, {class = "Warden", func = function(current) snd.warden_bash() end}, {class = "Bard", func = function(current) snd.bard_bash() end}, {class = "Alchemist", func = function(current) snd.alchemist_bash() end}, {class = "Runecarver", func = function(current) snd.runecarver_bash() end}, {class = "None", func = function(current) snd.astral_bash() end}, }
function snd.bashing_function() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if not snd.room_clear then return end if not snd.waiting.queue then if not snd.bashing.targeted then snd.runBasher() else battack = "" for k,v in pairs(snd.class_list) do if snd.class_list[k].class == snd.class then v.func() if snd.have_aff("shock") then battack = "overdrive"..snd.sep..battack end end end if tonumber(snd.toggles.gauntlet_level) >= 2 and snd.balance.barrier and not snd.liarstrummed then if snd.elementalcount < 50 then battack = battack..snd.sep.."absorb ylem" ..snd.sep else battack = "sacrifice corpses"..snd.sep..battack..snd.sep.."absorb ylem"..snd.sep end else if snd.elementalcount < 50 then battack = battack else battack = "sacrifice corpses"..snd.sep..battack end end if snd.probed then else if gmcp.Room.Info.area == "Tcanna Island" or gmcp.Room.Info.area == "Drakuum" or gmcp.Room.Info.area == "the Torturers' Caverns" then battack = "probe "..snd.bashing.target..snd.sep..battack end end if tonumber(gmcp.Char.Vitals.bleeding) > 500 and hasSkill("Clotting") then if mp >= 50 then battack = battack..snd.sep.."clot 500"..snd.sep end end
if battack ~= snd.last_attack and snd.ylem_check == false then snd.last_attack = battack snd.send("qeb stand"..snd.sep..snd.last_attack) snd.waiting.queue = true tempTimer(snd.delay(), [[snd.waiting.queue = false]]) end end end end
function snd.templar_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if snd.counterattack_active then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."touch shield" end elseif hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") then battack = battack..snd.sep.."battlefury dsw "..snd.bashing.target.." sacrifice sacrifice" else battack = battack..snd.sep.."battlefury dsk "..snd.bashing.target.." sacrifice sacrifice" end elseif snd.pushattack then battack = battack.. "push "..snd.bashing.target snd.pushattack = false elseif snd.toggles.newbie then battack = battack.. "strikebash "..snd.bashing.target elseif snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") then battack = battack.. "dsw "..snd.bashing.target else battack = battack.. "dsk "..snd.bashing.target end
end
