if snd.counterattack_active then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."touch shield" end elseif hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") then battack = battack..snd.sep.."fell "..snd.bashing.target.." duskosa duskosa" else battack = battack..snd.sep.."dpl "..snd.bashing.target.." duskosa duskosa" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false elseif snd.toggles.newbie then battack = battack..snd.sep.."rivebash "..snd.bashing.target elseif snd.wielded.left.name:find("warhammer") or snd.wielded.left.name:find("bastard") then battack = battack..snd.sep.."fell "..snd.bashing.target else battack = battack..snd.sep.."dpl "..snd.bashing.target end
