function snd.luminary_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."angel aura me" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false elseif snd.toggles.lightning then battack = battack..snd.sep.."evoke lightning "..snd.bashing.target else battack = battack..snd.sep.."smite "..snd.bashing.target end
end
