if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."OSSO RIBCAGE me" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false elseif snd.toggles.lightning then battack = battack..snd.sep.."TECTONIC ASHFALL "..snd.bashing.target else battack = battack..snd.sep.."quash "..snd.bashing.target.." secret" end
