function snd.monk_bash()
hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if mp >= 30 and hp <= 75 and snd.transmutation then battack = battack..snd.sep.."transmute mana 1000"..snd.sep tempTimer(2,[[snd.transmutation = true]]) end
if not snd.numbness_used then if hp <= 70 then battack = battack..snd.sep.."numb"..snd.sep end end
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."touch shield" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false elseif snd.stance ~= "scorpion" and not snd.toggles.cobra then battack = battack..snd.sep.."scs"..snd.sep elseif snd.toggles.cobra then if snd.stance ~= "cobra" then battack = battack..snd.sep.."cbs" else battack = battack..snd.sep.."combo "..snd.bashing.target.." sdk sdk" end else battack = battack..snd.sep.."combo "..snd.bashing.target.." sdk ucp ucp" end
end
