if snd.familiar and snd.balance.morph then if snd.shamanpet == "bear" then battack = battack..snd.sep.."order spirit passive"..snd.sep.."familiar morph spider"..snd.sep elseif snd.shamanpet == "spider" then battack = battack..snd.sep.."order spirit passive"..snd.sep.."familiar morph bear"..snd.sep else battack = battack..snd.sep.."order spirit passive"..snd.sep.."familiar morph bear"..snd.sep end else battack = battack..snd.sep.."familiar recall"..snd.sep.."order spirit passive"..snd.sep.."order spirit follow me"..snd.sep end
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."touch shield" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false else if wp>=45 and tonumber(gmcp.Char.Vitals.energy) > 3 then battack = battack..snd.sep.."commune boost"..snd.sep.."commune lightning "..snd.bashing.target else battack = battack..snd.sep.."commune lightning "..snd.bashing.target end end
end
