end
function snd.sciomancer_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) if snd.reflection_charge == 3 then snd.reflected = false end
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then if snd.reflection_charge > 0 and not snd.reflected then battack = battack..snd.sep.."cast reflection" else battack = battack..snd.sep.."jig" end else battack = battack..snd.sep.."touch shield" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false elseif snd.toggles.newbie then battack = battack..snd.sep.."cast ruin "..snd.bashing.target else if table.contains(gmcp.Char.Defences.List, "shadow_mantle") then if tonumber(gmcp.Char.Vitals.shadowprice) < 4 then battack = battack..snd.sep.."shadowprice gloom " ..snd.bashing.target..snd.sep.."cast fever "..snd.bashing.target else battack = battack..snd.sep.."cast fever " ..snd.bashing.target end else if tonumber(gmcp.Char.Vitals.shadowprice) < 2 then battack = battack..snd.sep.."shadowprice gloom " ..snd.bashing.target..snd.sep.."cast fever "..snd.bashing.target else battack = battack..snd.sep.."cast fever " ..snd.bashing.target end end end
end
function snd.bard_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."sing song of youth" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false else if tonumber(gmcp.Char.Vitals.dithering) > 0 then battack = battack..snd.sep.."tempo "..snd.bashing.target else battack = battack..snd.sep.."weave tearing "..snd.bashing.target end end
end
function snd.tidesage_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."fog harden me" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false else battack = battack..snd.sep.."tide clobber "..snd.bashing.target if snd.defenses.def_hightide.state == "down" then battack = battack..snd.sep.."synth recuperate" end end
end
function snd.alchemist_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp)
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then battack = battack..snd.sep.."jig" else battack = battack..snd.sep.."touch shield" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false else battack = battack..snd.sep.."alchemy catalyse"..snd.sep.."alchemy virulent "..snd.bashing.target end
end
function snd.astral_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) if snd.reflection_charge == 3 then snd.reflected = false end
if tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if mp >= 50 then if snd.shield_check and not snd.punisher then battack = "clot 500"..snd.sep.."touch shield" else battack = "clot 500" end end elseif snd.counterattack_active or snd.have_aff("blackout") then if snd.punisher then if snd.reflection_charge > 0 and not snd.reflected then battack = "point wand at me" else if snd.balance.barrier and not snd.liarstrummed then battack = "strum lyre" snd.liarstrummed = true tempTimer(1.5,[[snd.liarstrummed = false]]) else battack = "jig" end end else battack = "touch shield" end elseif snd.pushattack then battack = "push "..snd.bashing.target snd.pushattack = false elseif (hp <= 75 or snd.have_aff("paresis") or snd.have_aff("asthma") or snd.have_aff("slickness") or snd.have_aff("aeon")) and not snd.self_prone() then battack = "astral ambrosia" else if snd.counterattack_active then battack = battack.."jig" else battack = "astral evert "..snd.bashing.target end end end
function snd.runecarver_bash() hp = 100*(gmcp.Char.Vitals.hp/gmcp.Char.Vitals.maxhp) mp = 100*(gmcp.Char.Vitals.mp/gmcp.Char.Vitals.maxmp) if snd.reflection_charge == 3 then snd.reflected = false end
if snd.counterattack_active or snd.have_aff("blackout") or hp <= 75 or tonumber(gmcp.Char.Vitals.bleeding) > 1000 then if snd.punisher then if snd.reflection_charge > 0 and not snd.reflected then battack = battack..snd.sep.."runecarve branch" else battack = battack..snd.sep.."jig" end else battack = battack..snd.sep.."touch shield" end elseif snd.pushattack then battack = battack..snd.sep.."push "..snd.bashing.target snd.pushattack = false elseif snd.toggles.newbie then battack = battack..snd.sep.."hex enervate "..snd.bashing.target else if table.contains(gmcp.Char.Defences.List, "hex_preservation") then if tonumber(gmcp.Char.Vitals.spiritpact) < 4 then battack = battack..snd.sep.."spiritpact despair " ..snd.bashing.target..snd.sep.."hex fever "..snd.bashing.target else battack = battack..snd.sep.."hex fever " ..snd.bashing.target end else if tonumber(gmcp.Char.Vitals.spiritpact) < 2 then battack = battack..snd.sep.."spiritpact despair " ..snd.bashing.target..snd.sep.."hex fever "..snd.bashing.target else battack = battack..snd.sep.."hex fever " ..snd.bashing.target end end end
end

function snd.runRock()
if bot.going then expandAlias("move", false) end
snd.core() end

