function snd_bashAreas(areas_chosen) local areas = string.split(areas_chosen, ", ") local num_areas = #areas - 1 local full_list_of_areas = mmp.deepcopy(sunder_areaPaths) if not sunder_areaPaths then snd.message("Area table not found.") else local path_being_constructed = {} cecho("\n<CadetBlue>We are checking your area request:") for _, v in pairs(areas) do for x, y in pairs(full_list_of_areas) do area_being_checked = x:lower() if area_being_checked:find(v) then cecho("\n<MediumSeaGreen>Found: "..x) local area_to_be_added = full_list_of_areas[x] if #path_being_constructed ~= 0 then for l, m in pairs(path_being_constructed) do local areas_added = 0 for a, b in pairs(m) do if b == "pthcity" and areas_added < num_areas then display(x) path_being_constructed[l][a] = snd.bashing_area_beginning_rooms[x] areas_added = areas_added + 1 end --21 end--20 end--18 end--17 table.insert(path_being_constructed, area_to_be_added) break end end end
local final_path = {}
for myareastobash, roomsinthoseareas in pairs(path_being_constructed) do for first_room, next_room in pairs(roomsinthoseareas) do final_path[first_room] = next_room end end
snd.message("If that's right, "..gmcp.Char.Status.name..", head on over to the first area!") bot.going = true bot.path = final_path if not snd.toggles.fasthunt then expandAlias("ff") end expandAlias("aa") end
end
