function snd.akkari_host() if gmcp.Char.Status.spec == "Dosan" then if table.isMember(snd.primary_aff, "blood_curse") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_curse")) end if table.isMember(snd.primary_aff, "blood_poison") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_poison")) end if table.isMember(snd.primary_aff, "blood_rune") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_rune")) end elseif gmcp.Char.Status.spec == "Il'ahji" then if table.isMember(snd.primary_aff, "feast") then table.remove(snd.primary_aff, table.index_of(table, "feast")) end if table.isMember(snd.primary_aff, "blood_curse") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_curse")) end if table.isMember(snd.primary_aff, "blood_rune") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_rune")) end elseif gmcp.Char.Status.spec == "Naarak" then if table.isMember(snd.primary_aff, "feast") then table.remove(snd.primary_aff, table.index_of(table, "feast")) end if table.isMember(snd.primary_aff, "blood_poison") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_poison")) end else if table.isMember(snd.primary_aff, "feast") then table.remove(snd.primary_aff, table.index_of(table, "feast")) end if table.isMember(snd.primary_aff, "blood_poison") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_poison")) end if table.isMember(snd.primary_aff, "blood_curse") then table.remove(snd.primary_aff, table.index_of(snd.primary_aff, "blood_curse")) end end end
function snd.akkari_attend() if gmcp.Char.Status.spec == "Il'ahji" or gmcp.Char.Status.spec == "Naarak" or snd.can_attend == false then if table.isMember(snd.secondary_aff, "hearing") then table.remove(snd.secondary_aff, table.index_of(snd.secondary_aff, "hearing")) end if table.isMember(snd.secondary_aff, "sight") then table.remove(snd.secondary_aff, table.index_of(snd.secondary_aff, "sight")) end end end
function snd.can_deliver() if snd.checkSomeAffs({"paralysis", "paresis", "right_arm_broken", "left_arm_broken", "right_leg_broken", "left_leg_broken", "haemophilia", "asthma", "impairment", "clumsiness", "vomiting", "sensitivity", "ringing_ears", "lethargy", "blood_poison", "blood_curse", "crippled_throat", "slough", "disfigurement", "blisters", "lightwound", "ablaze", "hypothermia", "shivering", "frozen", "limp_veins", "rend", "exhausted", "allergies", "addiction", "hypersomnia", "heartflutter", "whiplash", "backstrain", "sore_wrist", "sore_ankle", "burnt_eyes", "blurry_vision", "migraine", "deadening", "ice_encased",}, 1) and snd.proned() then return true else return false end end
function snd.akkari_behead() if snd.target_locked() and snd.checkSomeAffs({"disrupted", "writhe_transfixed",}, 2) then return true else return false end
end

