snd.e = {
	echo	= function (str, lnb, lna)
		if lnb then echo("\n") end
		cecho(string.format("%s", "<aquamarine>Target Update: <grey>") .. str:title())
		if lna then echo("\n") end
	end,

	warn	= function (str, lnb, lna)
		if lnb then echo("\n") end
		cecho(string.format("%s", "<orange_red>Warning:<navajo_white> ") .. str:title())
		if lna then echo("\n") end
	end,
	}

Script.script:
function snd.handleFitness()
 snd.target_cured("asthma")
end

function snd.handleRestore() local limbs = {"right arm", "left arm", "left leg", "right leg"}
for _, v in pairs(limbs) do
if snd.checkAff(v:gsub(" ", "_").."_broken") and snd.limb_status[v] == "healed" then snd.target_cured(v:gsub(" ", "_").."_broken") end end end

snd.timers = snd.timers or {} snd.timers.pill = snd.timers.pill or {} snd.timers.focus = snd.timers.focus or {} snd.timers.tree = snd.timers.tree or {} snd.timers.restoration = snd.timers.restoration or {} snd.timers.rebounding = snd.timers.rebounding or {} snd.timers.induce = snd.timers.induce or {}
for k, _ in pairs(snd.timers) do snd.timers[k] = {["start"] = nil, ["limit"] = nil} end
function snd.timer_check(which, time_left, mode) if mode == nil then mode = "more than" end if snd.timers[which] ~= nil and snd.timers[which]["start"] ~= nil and snd.timers[which]["limit"] ~= nil then local now = os.clock() local endtime = snd.timers[which]["start"] + snd.timers[which]["limit"] if mode == "more than" then if endtime - now > time_left then return true else return false end elseif mode == "less than" then if endtime - now < time_left and (balance ~= "rebounding" or endtime - now > 0) then return true else return false end end else if mode == "less than" and balance ~= "rebounding" then return true else return false end end end

