"troll", "rat", "bush", "farmer", "oak", "serpent", "minion", "crocodile", "bobcat", "servant", "blood-fiend", "elephant", "rojalli", "aberration", "goose", "turtle", "crab", "icewyrm", "man", "rabbit", "wraith", "elk", }

function rainbow(args) local str = "" local colours = { "|cFF0000", "|cFF6600", "|cFFEE00", "|c00FF00", "|c0099FF", "|c4400FF", "|c9900FF" } local pass = math.random(7)
for char in args:gmatch"." do if char == " " then str = str .. char else str = str .. colours[pass] .. char if pass == #colours then pass = 1 end pass = pass + 1 end end
return hecho(str) end

function snd.toil_function()
if snd.toilup and not snd.toilon and #snd.group > 0 then snd.send("qeb runecarve toil on") elseif not snd.toilup and snd.toilon and #snd.group > 0 then snd.send("qeb runecarve toil off") end
end

function snd.metawake_function()
if snd.defenses.def_metawake.state ~= "deffed" and snd.metawakeon then snd.send("qeb metawake on") else snd.send("qeb metawake off") end
end

function snd.do_rage() if snd.rage_check() and snd.can_rage() and snd.balance.rage then snd.send("rage") echo("(rage)") snd.last_type = "rage" snd.last_item = "rage" snd.waiting.balance = true tempTimer(snd.delay(), [[snd.waiting.balance = false]]) end end
function snd.rage_check()
if not snd.balance.rage then return false end
if not gmcp then return false end if hp ~= nil then if hp <= 1 then return false end end
local rage_affs = { "aff_hubris", "aff_pacifism", "aff_peace", "aff_agony", "aff_accursed", "aff_limp_veins", "aff_lovers_effect", "aff_laxity", "aff_superstition", "aff_generosity", "aff_justice", "aff_magnanimity",}
for _, v in pairs(rage_affs) do if snd.have_aff(v) then return true end end return false end

function snd.do_restoration()
if snd.have_aff("spinal_rip") then return false end
if snd.restore_check() and snd.can_restore() and snd.balance.restore and snd.full_balance() then send("restore") snd.last_type = "restore" snd.last_item = "restore" snd.waiting.balance = true tempTimer(snd.delay(), [[snd.waiting.balance = false]]) end end
function snd.restore_check()
if not snd.balance.restore then return false end
local restore_affs = { "left_arm_crippled", "left_leg_crippled", "right_arm_crippled", "right_leg_crippled", }
local limbcount = 0 for _, v in pairs(restore_affs) do if snd.have_aff(v) and snd.not_aff(v:gsub("crippled", "broken")) and snd.not_aff(v:gsub("crippled", "mangled")) then limbcount = limbcount + 1 end end if limbcount >= 2 then return true else return false end end
function snd.can_restore() if hasSkill("Restoration") and snd.balance.restore then return true else return false end end

function snd.do_regenerate()
if snd.regenerate_check() and snd.can_regenerate() and snd.balance.regenerate and snd.full_balance() then send("regenerate") snd.last_type = "regenerate" snd.last_item = "regenerate" snd.waiting.balance = true tempTimer(snd.delay(), [[snd.waiting.balance = false]]) end end
function snd.regenerate_check()
if not snd.balance.regenerate then return false end
local regenerate_affs = { "left_arm_broken", "left_leg_broken", "right_arm_broken", "right_leg_broken", "torso_broken", "head_broken", }
for _, v in pairs(regenerate_affs) do if snd.have_aff(v) then return true end end return false end
function snd.can_regenerate() if hasSkill("Regenerate") and snd.balance.regenerate then return true else return false end end

function snd.initiate()
