--Zealot ["Zeal"] = { Swagger = function(target) snd.target_cured("paresis") end, Anklepin = function(target) snd.target_got("sore_ankle") end, Jawcrack = function(target) snd.target_got("stuttering") end, Descent = function(target) snd.target_got("backstrain") end, Wristlash = function(target) snd.target_got("sore_wrist") end, Uprise = function(target) snd.target_got("whiplash") end, Blitz = function(target, conditional) snd.blitz = true if conditional == "start" then enableTrigger("Blitz Affs") else disableTrigger("Blitz Affs") end end, Palmforce = function(target) snd.target_got("fallen") end, Dislocate = function(target, limb) table.insert(snd.maybe_affs, 1, limb.."_dislocated") snd.onHit2({"Dodge", "Parry"}) end, Twinpress = function(target) table.insert(snd.maybe_affs, "stiffness") table.insert(snd.maybe_affs, "muscle_spasms") snd.onHit2({"Dodge"}, "Zealot Twinpress") end, Clawtwist = function(target) snd.last_hit_limb = "torso" snd.last_limb_dmg = 8.5 snd.took_limb_dmg(snd.last_hit_limb, snd.last_limb_dmg) snd.onHit2({"Parry", "Dodge"}) end, Pummel = function(target, limb) snd.last_hit_limb = limb snd.last_limb_dmg = 9.5 snd.took_limb_dmg(snd.last_hit_limb, snd.last_limb_dmg) snd.onHit2({"Parry", "Dodge"}) end, Wanekick = function(target, limb) snd.last_hit_limb = limb snd.last_limb_dmg = 9 snd.took_limb_dmg(snd.last_hit_limb, snd.last_limb_dmg) snd.onHit2({"Parry", "Dodge"}) end, Risekick = function(target) snd.last_hit_limb = "torso" snd.last_limb_dmg = 5 snd.took_limb_dmg("torso", snd.last_limb_dmg) snd.onHit2({"Parry", "Dodge"}) end, Edgekick = function(target) snd.last_hit_limb = "head" snd.last_limb_dmg = 3.5 snd.took_limb_dmg("head", snd.last_limb_dmg) table.insert(snd.maybe_affs, 1, "crippled_throat") snd.onHit2({"Parry", "Dodge"}) end, Sunkick = function(target) snd.last_hit_limb = "head" snd.last_limb_dmg = 6 snd.took_limb_dmg("head", snd.last_limb_dmg) if not snd.checksomeAffs({"stupidity", "dizziness"}, 1) then table.insert(snd.maybe_affs, 1, "stupidity") table.insert(snd.maybe_affs, 2, "dizziness") elseif snd.checkAff("stupidity") then table.insert(snd.maybe_affs, 1, "dizziness") else table.insert(snd.maybe_affs, 1, "stupidity") end snd.onHit2({"Dodge", "Parry"}, "Zealot Sunkick") end, Direblow = function(target) enableTrigger("Direblow Hit Confirmation") end, Heelrush = function(target) enableTrigger("Heelrush") end,
},
