snd.secondary_affs = { "confusion", "fear", "impatience", "paranoia", "stupidity", "agoraphobia", "masochism", "lovers", "contrition", "epilepsy", "anorexia", "amnesia", "peace", "dementia", "berserking", "indifference", "vertigo", "remorse", "recklessness", "sight", "hearing", "blood_poison", }
