function snd.venomlimbmoderev()
if snd.lefttargetting ~= "none" or snd.righttargetting ~= "none" then snd.queueattack = snd.queueattack.. "wipe left" ..snd.separator.. "wipe right" ..snd.separator.. "target nothing" ..snd.separator.. "target nothing with right" ..snd.separator else snd.queueattack = snd.queueattack.. "wipe left" ..snd.separator.. "wipe right" ..snd.separator end snd.limbvenomone() snd.limbvenomtwo() local mark = "none" if tonumber(gmcp.Char.Vitals.charge_left) >= 101 or tonumber(gmcp.Char.Vitals.charge_right) >= 101 then mark = "telvi" elseif tonumber(gmcp.Char.Vitals.charge_left) >= 40 and tonumber(gmcp.Char.Vitals.charge_right) >= 40 then -- attacks before mark = "lawid" end --snd.chiromark if mark == "telvi" then if tonumber(gmcp.Char.Vitals.charge_left) >= 101 and tonumber(gmcp.Char.Vitals.charge_right) >= 101 then if not toggleVarsClass.twohandmode then snd.queueattack = snd.queueattack.. "CHIROGRAPHY BLADE SCRIBE LEFT telvi" ..snd.separator.. "CHIROGRAPHY BLADE SCRIBE right telvi" ..snd.separator.. "dpl " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator else snd.queueattack = snd.queueattack.. "CHIROGRAPHY BLADE SCRIBE LEFT telvi" ..snd.separator.. "fell " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator end snd.queueaffcall = snd.queueaffcall.. "Afflicting "..snd.aim..": " ..snd.scythevenom1.. ", " ..snd.scythevenom2 elseif tonumber(gmcp.Char.Vitals.charge_right) >= 101 then -- for fell only is left if not toggleVarsClass.twohandmode then snd.queueattack = snd.queueattack.. "CHIROGRAPHY BLADE SCRIBE right telvi" ..snd.separator.. "dpl " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator end snd.queueaffcall = snd.queueaffcall.. "Afflicting "..snd.aim..": " ..snd.scythevenom1.. ", " ..snd.scythevenom2 elseif tonumber(gmcp.Char.Vitals.charge_left) >= 101 then if not toggleVarsClass.twohandmode then snd.queueattack = snd.queueattack.. "CHIROGRAPHY BLADE SCRIBE left telvi" ..snd.separator.. "dpl " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator else snd.queueattack = snd.queueattack.. "CHIROGRAPHY BLADE SCRIBE left telvi" ..snd.separator.. "fell " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator end snd.queueaffcall = snd.queueaffcall.. "Afflicting "..snd.aim..": " ..snd.scythevenom1.. ", " ..snd.scythevenom2 end elseif mark == "lawid" and toggleVars.teammode then if not toggleVarsClass.twohandmode then snd.queueattack = snd.queueattack.. "chirography blade scribe left lawid" ..snd.separator.. "chirography blade scribe right lawid" ..snd.separator.. "dpl " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator else snd.queueattack = snd.queueattack.. "chirography blade scribe left lawid" ..snd.separator.. "fell " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator end else if not toggleVarsClass.twohandmode then snd.queueattack = snd.queueattack.. "dpl " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator else snd.queueattack = snd.queueattack.. "fell " ..snd.aim.. " " ..snd.scythevenom1.. " " ..snd.scythevenom2.. "" ..snd.separator end snd.queueaffcall = snd.queueaffcall.. "Afflicting "..snd.aim..": " ..snd.scythevenom1.. ", " ..snd.scythevenom2 end
end

