function snd.deformchecker() snd.deformcount = 0 for i = 1, tonumber(#snd.deformvenoms) do local affliction = snd.deformvenoms[i] if table.contains(snd.target_has, affliction) then snd.deformcount = snd.deformcount + 1 end end end

