function display_my_affs()
local self_display_table = {} local tally_check = snd.aff_tally() local aff_check = "" if tally_check > 0 then for aff in pairs(snd.afflictions) do if snd.have_aff(aff) then aff_check = aff aff_check = aff_check:gsub("aff_", "") aff_check = aff_check:gsub("_", " ") aff_check = aff_check:gsub("left", "L") aff_check = aff_check:gsub("right", "R") if snd.afflictions[aff]["cures"]["herb"] ~= nil or snd.afflictions[aff]["cures"]["salve"] ~= nil or snd.afflictions[aff]["cures"]["smoke"] ~= nil then table.insert(self_display_table, "<center>"..aff_check.."</center>") end end end
myaffs:echo(table.concat(self_display_table)) else myaffs:echo("<center>my affs</center>") end color_self_affs() end

