Trigger.script:
if matches[2] == snd.target then
 tempTimer(5.5, [[
 								snd.used.rebounding = true;
                snd.waiting_queue = false;
                if snd.toggles.attacking then snd.attack_function() end
							]])

end

local basic_limb_affs = { ["left leg"] = "a broken left leg", ["right leg"] = "a broken right leg", ["left arm"] = "a broken left arm", ["right arm"] = "a broken right arm", }
if matches[2] == snd.target then snd.target_got(basic_limb_affs[matches[3]]) end
if snd.faction == "shadow" then if matches[4] == "raloth" then snd.have_raloth = true tempTimer(5, [[snd.have_raloth = false]]) end end

local fury_affs = { ["Warcry"] = "hearing", ["Threaten"] = "hatred", ["Shake"] = "weariness", ["Halt"] = "lethargy", ["Exhaust"] = "exhausted", }
if matches[3] == snd.target then snd.target_got(fury_affs[matches[2]]) end
if matches[1]:find("You") then snd.balance.fury = false end

if not snd.toggles.bashing then send("ql")
local who = matches[2]
if matches[2] == snd.target then if snd.offense_loaded then snd.reset_target() end if swarm.targeting.auto then expandAlias("nt") if #swarm.targeting.list == 0 then string = "Out of targets! Hopefully switching to normal calling from snd.shadow!" snd.no_target() end else snd.no_target() end end end

setTriggerStayOpen("venoms missing", 0)
local all_venoms = { "sumac", "xentio", "oleander", "eurypteria", "kalmia", "digitalis", "darkshade", "curare", "epteth", "prefarar", "monkshood", "euphorbia", "colocasia", "oculus", "hepafarin", "camus", "vernalius", "epseth", "larkspur", "slike", "voyria", "delphinium", "vardrax", "loki", "aconite", "selarnia", "gecko", "scytherus", "ouabain" }
local missing_venoms = {}
for venom in pairs(all_venoms) do if not table.contains(snd.my_venoms, all_venoms[venom]) then table.insert(missing_venoms, all_venoms[venom]) end end if #missing_venoms >= 1 then snd.message("You are missing: <red>"..table.concat(missing_venoms, "<white>,<red> ")) snd.send(" ") else snd.message("You have all the venoms!") snd.send(" ") end
